import request from '@/api/request.js'
// 获取首页信息
function getInfo(data) {
  return request({
    url: '/search/indexaction',
    method: 'get',
    params: data
  })
}
// 删除历史记录
function deleteHistory(data) {
  return request({
    url: '/search/clearhistoryAction',
    method: 'post',
    data: data
  })
}
export { getInfo, deleteHistory }
