import { Toast } from 'vant';
import { areaList } from '@vant/area-data';
import { addressInfo, addNewAddr } from '@/api/address/index.js';
export default {
  data() {
    return {
      areaList,
      searchResult: [],
      addAddressInfo: {}
    };
  },
  methods: {
    // 点击返回按钮
    onClickLeft() {
      this.$router.go(-1);
    },
    onSave(content) {
      this.addAddressInfo = content;
      // address addressId checked detailadress openId telNumber userName
      const data = {
        address: content.province + content.city + content.county,
        checked: content.isDefault,
        detailadress: content.addressDetail,
        openId: localStorage.getItem('openId'),
        telNumber: content.tel,
        userName: content.name
      };
      addNewAddr({
        data
      }).then(res => {
        console.log(res);
      });
    },
    onDelete() {
      Toast('delete');
    },
    onChangeDetail(val) {
      if (val) {
        this.searchResult = [{
          name: '黄龙万科中心',
          address: '杭州市西湖区'
        }];
      } else {
        this.searchResult = [];
      }
    },
    // 获取地址信息
    getAddressInfo() {
      addressInfo({
        openId: localStorage.getItem('openId')
      }).then(res => {
        console.log(res);
      });
    }
  },
  created() {
    this.getAddressInfo();
  }
};
